4:"link";a:1:{i:0;a:5:{s:4:"data";s:82:"https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:91:"https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 27 Nov 2015 16:00:11 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:6:"Events";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:11:"Field Notes";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:37:"http://en.blog.wordpress.com/?p=33500";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:373:"At the fifth annual Wine Tourism Conference in Virginia, Happiness Engineers Andrea Badgley and Marjorie R. Asturias talked about wine tourism and digital marketing with industry professionals.<img alt="" border="0" src="https://pixel.wp.com/b.gif?host=en.blog.wordpress.com&#038;blog=3584907&#038;post=33500&#038;subd=en.blog&#038;ref=&#038;feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:14:"Andrea Badgley";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:14110:"<p><em><a href="https://automattic.com/about/">Automatticians</a>, the people who build WordPress.com, participate in events and projects around the world every day. Periodically, they report back on the exciting things they do when not in front of a computer.</em></p>\n<p><em>Last week, Happiness Engineers <a href="http://marjorieasturias.com/">Marjorie R. Asturias</a> and <a href="http://andreabadgley.com/">Andrea Badgley</a> attended the fifth annual <a href="http://winetourismconference.org/">Wine Tourism Conference</a> in Loudoun County, Virginia. The event brings together leaders in the wine industry &#8212; from tour operators to wine bloggers &#8212; to talk about wine tourism and digital marketing (and to share a glass or two). Here&#8217;s Andrea&#8217;s account of her experience at the conference.<br />\n</em></p>\n<p>Vintners and tour operators from Canada, the UK, and Portugal flew into Virginia last week to connect and talk about how to reach out to wine enthusiasts and welcome them to the growers’ tasting rooms.</p>\n<p>Tourism data or emerging markets aside, these folks are passionate about wine. They’d be crafting, drinking, and talking about it even if wine tourism weren’t a growing industry.</p>\n<blockquote><p>You know you&#8217;re in the right place when at 9 o&#8217;clock in the morning, you&#8217;re talking about wine.</p>\n<p style="text-align:right;"><small>&#8212; Beth Erickson, president of <a href="http://www.visitloudoun.org/">Visit Loudoun</a></small></p>\n</blockquote>\n<p>Marjorie and I enjoyed meeting and connecting at a one-on-one level with the 175 conference attendees to hear their personal stories. Most proprietors we met operate small, intimate wineries, where they do all of the work, from growing the grapes to bottling the wine. The couple who owns the <a href="http://www.lafinquitawinery.com/">La Finquita</a> winery cultivate and harvest their grapes themselves, and they even custom etch their wine bottles. Similarly, tour operators execute every aspect of their business, from booking the tours to balancing the books, and from managing the website to driving the bus. They do this work because they are passionate about it.</p>\n<p>\n<a href=''https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/stone-tower-12/''><img width="150" height="100" src="https://en-blog.files.wordpress.com/2015/11/stone-tower-12.jpg?w=150&#038;h=100" class="attachment-thumbnail size-thumbnail" alt="Lunch wine at Stone Towers Winery WTC15" data-attachment-id="33509" data-orig-file="https://en-blog.files.wordpress.com/2015/11/stone-tower-12.jpg" data-orig-size="3008,2000" data-comments-opened="1" data-image-meta="{&quot;aperture&quot;:&quot;1.8&quot;,&quot;credit&quot;:&quot;&quot;,&quot;camera&quot;:&quot;NIKON D50&quot;,&quot;caption&quot;:&quot;&quot;,&quot;created_timestamp&quot;:&quot;1447897156&quot;,&quot;copyright&quot;:&quot;&quot;,&quot;focal_length&quot;:&quot;50&quot;,&quot;iso&quot;:&quot;0&quot;,&quot;shutter_speed&quot;:&quot;0.0015625&quot;,&quot;title&quot;:&quot;&quot;,&quot;orientation&quot;:&quot;1&quot;}" data-image-title="Pouring wine at Wine Tourism Conf" data-image-description="" data-medium-file="https://en-blog.files.wordpress.com/2015/11/stone-tower-12.jpg?w=300" data-large-file="https://en-blog.files.wordpress.com/2015/11/stone-tower-12.jpg?w=635" /></a>\n<a href=''https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/stone-tower-wine-pour-15/''><img width="150" height="100" src="https://en-blog.files.wordpress.com/2015/11/stone-tower-wine-pour-15.jpg?w=150&#038;h=100" class="attachment-thumbnail size-thumbnail" alt="Wine pouring at lunch" data-attachment-id="33510" data-orig-file="https://en-blog.files.wordpress.com/2015/11/stone-tower-wine-pour-15.jpg" data-orig-size="3008,2000" data-comments-opened="1" data-image-meta="{&quot;aperture&quot;:&quot;1.8&quot;,&quot;credit&quot;:&quot;&quot;,&quot;camera&quot;:&quot;NIKON D50&quot;,&quot;caption&quot;:&quot;&quot;,&quot;created_timestamp&quot;:&quot;1447897269&quot;,&quot;copyright&quot;:&quot;&quot;,&quot;focal_length&quot;:&quot;50&quot;,&quot;iso&quot;:&quot;0&quot;,&quot;shutter_speed&quot;:&quot;0.04&quot;,&quot;title&quot;:&quot;&quot;,&quot;orientation&quot;:&quot;1&quot;}" data-image-title="stone tower wine pour 15" data-image-description="" data-medium-file="https://en-blog.files.wordpress.com/2015/11/stone-tower-wine-pour-15.jpg?w=300" data-large-file="https://en-blog.files.wordpress.com/2015/11/stone-tower-wine-pour-15.jpg?w=635" /></a>\n<a href=''https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/marge-and-badgley-wtc15/''><img width="150" height="150" src="https://en-blog.files.wordpress.com/2015/11/marge-and-badgley-wtc15.jpg?w=150&#038;h=150" class="attachment-thumbnail size-thumbnail" alt="Marjorie Asturias and Andrea Badgley at Wine Tourism Conference 2015" data-attachment-id="33501" data-orig-file="https://en-blog.files.wordpress.com/2015/11/marge-and-badgley-wtc15.jpg" data-orig-size="2448,2448" data-comments-opened="1" data-image-meta="{&quot;aperture&quot;:&quot;2.4&quot;,&quot;credit&quot;:&quot;&quot;,&quot;camera&quot;:&quot;iPhone 5c&quot;,&quot;caption&quot;:&quot;&quot;,&quot;created_timestamp&quot;:&quot;1447871530&quot;,&quot;copyright&quot;:&quot;&quot;,&quot;focal_length&quot;:&quot;4.12&quot;,&quot;iso&quot;:&quot;400&quot;,&quot;shutter_speed&quot;:&quot;0.058823529411765&quot;,&quot;title&quot;:&quot;&quot;,&quot;orientation&quot;:&quot;1&quot;}" data-image-title="Automatticians at Wine Tourism Expo 2015" data-image-description="&lt;p&gt;Automattic and WordPress.com at Wine Tourism Conference 2015&lt;/p&gt;\n" data-medium-file="https://en-blog.files.wordpress.com/2015/11/marge-and-badgley-wtc15.jpg?w=300" data-large-file="https://en-blog.files.wordpress.com/2015/11/marge-and-badgley-wtc15.jpg?w=635" /></a>\n<a href=''https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/badgley-expo/''><img width="150" height="84" src="https://en-blog.files.wordpress.com/2015/11/badgley-expo.jpg?w=150&#038;h=84" class="attachment-thumbnail size-thumbnail" alt="Talking with WordPress users at the Expo" data-attachment-id="33524" data-orig-file="https://en-blog.files.wordpress.com/2015/11/badgley-expo.jpg" data-orig-size="720,405" data-comments-opened="1" data-image-meta="{&quot;aperture&quot;:&quot;0&quot;,&quot;credit&quot;:&quot;&quot;,&quot;camera&quot;:&quot;&quot;,&quot;caption&quot;:&quot;&quot;,&quot;created_timestamp&quot;:&quot;0&quot;,&quot;copyright&quot;:&quot;&quot;,&quot;focal_length&quot;:&quot;0&quot;,&quot;iso&quot;:&quot;0&quot;,&quot;shutter_speed&quot;:&quot;0&quot;,&quot;title&quot;:&quot;&quot;,&quot;orientation&quot;:&quot;0&quot;}" data-image-title="Badgley expo" data-image-description="" data-medium-file="https://en-blog.files.wordpress.com/2015/11/badgley-expo.jpg?w=300" data-large-file="https://en-blog.files.wordpress.com/2015/11/badgley-expo.jpg?w=635" /></a>\n<a href=''https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/lunch-pouring-2/''><img width="150" height="84" src="https://en-blog.files.wordpress.com/2015/11/lunch-pouring-2.jpg?w=150&#038;h=84" class="attachment-thumbnail size-thumbnail" alt="Lunch pouring" data-attachment-id="33525" data-orig-file="https://en-blog.files.wordpress.com/2015/11/lunch-pouring-2.jpg" data-orig-size="720,405" data-comments-opened="1" data-image-meta="{&quot;aperture&quot;:&quot;0&quot;,&quot;credit&quot;:&quot;&quot;,&quot;camera&quot;:&quot;&quot;,&quot;caption&quot;:&quot;&quot;,&quot;created_timestamp&quot;:&quot;0&quot;,&quot;copyright&quot;:&quot;&quot;,&quot;focal_length&quot;:&quot;0&quot;,&quot;iso&quot;:&quot;0&quot;,&quot;shutter_speed&quot;:&quot;0&quot;,&quot;title&quot;:&quot;&quot;,&quot;orientation&quot;:&quot;0&quot;}" data-image-title="lunch pouring 2" data-image-description="" data-medium-file="https://en-blog.files.wordpress.com/2015/11/lunch-pouring-2.jpg?w=300" data-large-file="https://en-blog.files.wordpress.com/2015/11/lunch-pouring-2.jpg?w=635" /></a>\n<a href=''https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/wp-bags-at-expo/''><img width="150" height="150" src="https://en-blog.files.wordpress.com/2015/11/wp-bags-at-expo.jpg?w=150&#038;h=150" class="attachment-thumbnail size-thumbnail" alt="WordPress.com and wine" data-attachment-id="33528" data-orig-file="https://en-blog.files.wordpress.com/2015/11/wp-bags-at-expo.jpg" data-orig-size="2448,2448" data-comments-opened="1" data-image-meta="{&quot;aperture&quot;:&quot;2.4&quot;,&quot;credit&quot;:&quot;&quot;,&quot;camera&quot;:&quot;iPhone 5c&quot;,&quot;caption&quot;:&quot;&quot;,&quot;created_timestamp&quot;:&quot;1447869862&quot;,&quot;copyright&quot;:&quot;&quot;,&quot;focal_length&quot;:&quot;4.12&quot;,&quot;iso&quot;:&quot;320&quot;,&quot;shutter_speed&quot;:&quot;0.05&quot;,&quot;title&quot;:&quot;&quot;,&quot;orientation&quot;:&quot;1&quot;}" data-image-title="WP bags at Expo" data-image-description="" data-medium-file="https://en-blog.files.wordpress.com/2015/11/wp-bags-at-expo.jpg?w=300" data-large-file="https://en-blog.files.wordpress.com/2015/11/wp-bags-at-expo.jpg?w=635" /></a>\n<a href=''https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/marjorie-wordpress-wtc15-expo/''><img width="150" height="150" src="https://en-blog.files.wordpress.com/2015/11/marjorie-wordpress-wtc15-expo.jpg?w=150&#038;h=150" class="attachment-thumbnail size-thumbnail" alt="Marjorie and WordPress.com at Expo" data-attachment-id="33527" data-orig-file="https://en-blog.files.wordpress.com/2015/11/marjorie-wordpress-wtc15-expo.jpg" data-orig-size="1746,1746" data-comments-opened="1" data-image-meta="{&quot;aperture&quot;:&quot;2.4&quot;,&quot;credit&quot;:&quot;&quot;,&quot;camera&quot;:&quot;iPhone 5c&quot;,&quot;caption&quot;:&quot;&quot;,&quot;created_timestamp&quot;:&quot;1447862440&quot;,&quot;copyright&quot;:&quot;&quot;,&quot;focal_length&quot;:&quot;4.12&quot;,&quot;iso&quot;:&quot;160&quot;,&quot;shutter_speed&quot;:&quot;0.05&quot;,&quot;title&quot;:&quot;&quot;,&quot;orientation&quot;:&quot;1&quot;}" data-image-title="Marjorie WordPress WTC15 Expo" data-image-description="" data-medium-file="https://en-blog.files.wordpress.com/2015/11/marjorie-wordpress-wtc15-expo.jpg?w=300" data-large-file="https://en-blog.files.wordpress.com/2015/11/marjorie-wordpress-wtc15-expo.jpg?w=635" /></a>\n<a href=''https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/marjorie-presenting/''><img width="150" height="150" src="https://en-blog.files.wordpress.com/2015/11/marjorie-presenting.jpg?w=150&#038;h=150" class="attachment-thumbnail size-thumbnail" alt="Designing Websites with Marjorie Asturias" data-attachment-id="33529" data-orig-file="https://en-blog.files.wordpress.com/2015/11/marjorie-presenting.jpg" data-orig-size="2209,2209" data-comments-opened="1" data-image-meta="{&quot;aperture&quot;:&quot;2.4&quot;,&quot;credit&quot;:&quot;&quot;,&quot;camera&quot;:&quot;iPhone 5c&quot;,&quot;caption&quot;:&quot;&quot;,&quot;created_timestamp&quot;:&quot;1448013589&quot;,&quot;copyright&quot;:&quot;&quot;,&quot;focal_length&quot;:&quot;4.12&quot;,&quot;iso&quot;:&quot;80&quot;,&quot;shutter_speed&quot;:&quot;0.05&quot;,&quot;title&quot;:&quot;&quot;,&quot;orientation&quot;:&quot;1&quot;}" data-image-title="Designing Websites for 2016 and Beyond at Wine Tourism Conference" data-image-description="&lt;p&gt;WordPress.com Designing Websites for 2016 and Beyond at Wine Tourism Conference 2015&lt;/p&gt;\n" data-medium-file="https://en-blog.files.wordpress.com/2015/11/marjorie-presenting.jpg?w=300" data-large-file="https://en-blog.files.wordpress.com/2015/11/marjorie-presenting.jpg?w=635" /></a>\n<br />\nThis passion is perfect for blogging. Everything has a story in wine tourism: the architecture of the tasting room, the land, the family, the wine. These are small businesses that feed naturally into the Go Local movement, in which customers want to meet the people who are growing and making local food and wine.</p>\n<p>Wine tour guides and emerging wineries are small businesses. They have minimal advertising budgets, but they have fascinating stories to tell. Websites, blogs, and social networking are powerful, inexpensive tools for connecting with their customers and getting the word out about the wine and land they love so much.</p>\n<div class="embed-mesh">\n<div style="width: 100%; position: relative; max-width: 635px" class="mesh-embed">\n<div style="padding-top: 100%"></div>\n<p><iframe src="https://me.sh/14aefik?oembed=635" style="position: absolute; top: 0; min-width: 200px; min-height: 200px; height: 100%" width="100%" height="500px" frameborder="0" allowfullscreen=""></iframe></div>\n</div>\n<p>Vintners poured wine and shared their stories and passion with us, and Marjorie and I were thrilled to return the favor. We talked about blogging as a guerrilla marketing tool, stressed the importance of having a website and an online presence, and even helped a local wine concierge, <a href="http://vino301.com/">Vino 301,</a> ensure her site is mobile ready. In addition, Marjorie presented a much-appreciated session on <a href="http://marjorieasturias.com/2015/11/20/wine-tourism-conference-presentation/">Designing Websites for 2016 and beyond</a>, and I succumbed to the <a href="http://andreabadgley.com/2015/11/20/tasting-wine-in-loudoun-county-virginia/">beauty of the region</a>, taking notes for when my husband and I one day get away to explore the wineries of Virginia.</p><br />Filed under: <a href=''https://en.blog.wordpress.com/category/events/''>Events</a>  <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gocomments/en.blog.wordpress.com/33500/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/comments/en.blog.wordpress.com/33500/" /></a> <img alt="" border="0" src="https://pixel.wp.com/b.gif?host=en.blog.wordpress.com&#038;blog=3584907&#038;post=33500&#038;subd=en.blog&#038;ref=&#038;feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:87:"https://en.blog.wordpress.com/2015/11/27/field-notes-wine-tourism-conference-2015/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:2:"12";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:2:{s:9:"thumbnail";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:1:{s:3:"url";s:72:"http://en-blog.files.wordpress.com/2015/11/stone-tower-wine-14.jpg?w=150";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"content";a:8:{i:0;a:6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:72:"http://en-blog.files.wordpress.com/2015/11/stone-tower-wine-14.jpg?w=150";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:29:"http://search.yahoo.com/mrss/";a:1:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:54:"Wine and WordPress.com at Wine Tourism Conference 2015";s:7:"attribs";a:1:{s:0:"";a:1:{s:4:"type";s:4:"html";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:1;a:6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:75:"https://0.gravatar.com/avatar/c946a587fd9b2d9e1f396f6996772fba?s=96&d=retro";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:29:"http://search.yahoo.com/mrss/";a:1:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:13:"andreabadgley";s:7:"attribs";a:1:{s:0:"";a:1:{s:4:"type";s:4:"html";}}s:8:"xml_base";s:0:"";s:17:"xml_ba