:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:12:"New Features";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:7:"Posting";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:4;a:5:{s:4:"data";s:7:"Writing";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:5;a:5:{s:4:"data";s:6:"Editor";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:37:"http://en.blog.wordpress.com/?p=32497";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:277:"We''re excited to unveil a faster, more streamlined way to create, share, and manage your content.<img alt="" border="0" src="https://pixel.wp.com/b.gif?host=en.blog.wordpress.com&#038;blog=3584907&#038;post=32497&#038;subd=en.blog&#038;ref=&#038;feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:9:"Kat Hagan";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:2944:"<p><img class="alignnone size-full wp-image-33145" src="https://en-blog.files.wordpress.com/2015/11/editor-announce1.png?w=635" alt="editor-announce"   /></p>\n<p>From parents and poets to journalists and politicians, WordPress.com’s publishing tools allow people to make their voices heard.</p>\n<p>We have heard your excellent feedback on our interim editor and today, we’re excited to introduce our new editor: a faster, cleaner, and more streamlined way to create posts and pages, and share, promote, and manage content across all your WordPress sites.</p>\n<p><img class="alignnone size-full wp-image-33153" src="https://en-blog.files.wordpress.com/2015/11/editor-posting-gif.gif?w=635" alt="editor-posting-gif"   /></p>\n<h3>Highlights: instant saving, quick sharing</h3>\n<ul>\n<li>It’s fast, responsive, and allows you to create posts and pages quickly on desktop and mobile devices.</li>\n<li>Easily manage your posts, whether you run an individual blog or wrangle multiple sites, authors, and posts.</li>\n<li>Access draft posts with one click so that you can iterate and revise quickly when inspiration strikes.</li>\n<li>Content is automatically saved, allowing you to focus and write &#8212; free of distraction.</li>\n<li>Drag and drop photos, music files, documents, and videos right into your post or page.</li>\n<li>Tags, categories, and sharing tools are at your fingertips, so you can make your content easier to find in the <a href="http://wordpress.com/">WordPress.com Reader</a> and across your social networks.</li>\n<li>Scheduling is a breeze with the revamped post calendar.</li>\n<li>It&#8217;s available for self-hosted WordPress.org sites, too! Just install the <a href="https://jetpack.me">Jetpack</a> plugin and activate <a href="https://jetpack.me/support/site-management/">Manage</a>.</li>\n</ul>\n<h3>A big thank you</h3>\n<p>We love that you’re passionate about WordPress.com, and most importantly, that you share that passion with us. If you’ve got feedback about the latest editor updates, we’d be grateful if you’d take a moment to share it with us in our <a href="https://en.forums.wordpress.com/topic/editor-feedback">support forum</a>.</p><br />Filed under: <a href=''https://en.blog.wordpress.com/category/editing/''>Editing</a>, <a href=''https://en.blog.wordpress.com/category/jetpack/''>Jetpack</a>, <a href=''https://en.blog.wordpress.com/category/new-features/''>New Features</a>, <a href=''https://en.blog.wordpress.com/category/posting/''>Posting</a>, <a href=''https://en.blog.wordpress.com/category/writing-2/''>Writing</a>  <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gocomments/en.blog.wordpress.com/32497/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/comments/en.blog.wordpress.com/32497/" /></a> <img alt="" border="0" src="https://pixel.wp.com/b.gif?host=en.blog.wordpress.com&#038;blog=3584907&#038;post=32497&#038;subd=en.blog&#038;ref=&#038;feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:68:"https://en.blog.wordpress.com/2015/11/16/new-high-speed-editor/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:3:"306";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:2:{s:9:"thumbnail";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:1:{s:3:"url";s:82:"http://en-blog.files.wordpress.com/2015/11/editor-desktop-screen-cropped.jpg?w=150";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"content";a:4:{i:0;a:6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:82:"http://en-blog.files.wordpress.com/2015/11/editor-desktop-screen-cropped.jpg?w=150";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:29:"http://search.yahoo.com/mrss/";a:1:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:29:"editor-desktop-screen-cropped";s:7:"attribs";a:1:{s:0:"";a:1:{s:4:"type";s:4:"html";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:1;a:6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:75:"https://1.grav