(774, '_transient_feed_00932a6779e7df3164fc4df499b4a6ca', 'a:4:{s:5:"child";a:1:{s:0:"";a:1:{s:3:"rss";a:1:{i:0;a:6:{s:4:"data";s:3:"\n\n\n";s:7:"attribs";a:1:{s:0:"";a:1:{s:7:"version";s:3:"2.0";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:0:"";a:1:{s:7:"channel";a:1:{i:0;a:6:{s:4:"data";s:49:"\n	\n	\n	\n	\n	\n	\n	\n	\n	\n	\n		\n		\n		\n		\n		\n		\n		\n		\n		\n	";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:3:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:17:"Google Earth Blog";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:25:"http://www.gearthblog.com";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:37:"The amazing things about Google Earth";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:13:"lastBuildDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 04 Dec 2015 10:02:13 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"language";a:1:{i:0;a:5:{s:4:"data";s:5:"en-US";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:9:"generator";a:1:{i:0;a:5:{s:4:"data";s:29:"http://wordpress.org/?v=4.3.1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"item";a:10:{i:0;a:6:{s:4:"data";s:45:"\n		\n		\n		\n		\n		\n				\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:24:"Uruguay gets Street View";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:77:"http://www.gearthblog.com/blog/archives/2015/12/uruguay-gets-street-view.html";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:86:"http://www.gearthblog.com/blog/archives/2015/12/uruguay-gets-street-view.html#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 04 Dec 2015 10:02:13 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:11:"Street View";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:11:"street view";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://www.gearthblog.com/?p=14364";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:584:"<p>Thank you to GEB readers Pedro and Ecksemmess for letting us know that Street View of Uruguay has recently been released. Street View changes (in red) between November 23rd, 2015 and December 3rd, 2015. Larger version. St. Elizabeth Church, Paso de los Toros The Street View vehicle takes a boat ride. Monument to the drowned. [&#8230;]</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/12/uruguay-gets-street-view.html">Uruguay gets Street View</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Timothy Whitehead";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:1519:"<p>Thank you to GEB readers Pedro and Ecksemmess for letting us know that Street View of Uruguay has recently been released.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/StreetViewTimeline2015-11-23to2015-12-03_small.jpg" alt="" /><br />\n<i>Street View changes (in red) between November 23rd, 2015 and December 3rd, 2015. <a href="http://www.gearthblog.com/wp-content/uploads/2015/12/StreetViewTimeline2015-11-23to2015-12-03.jpg">Larger version</a>.</i></p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/Uruguay1.jpg" alt="" /><br />\n<i><a href="https://en.wikipedia.org/wiki/Santa_Isabel,_Paso_de_los_Toros">St. Elizabeth Church, Paso de los Toros</a></i></p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/Uruguay2.jpg" alt="" /><br />\n<i>The Street View vehicle takes a boat ride.</i></p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/Uruguay3.jpg" alt="" /><br />\n<i>Monument to the drowned.</i></p>\n<p>To see the locations above in Google Earth download <a href="http://www.gearthblog.com/wp-content/uploads/2015/12/Uruguay-Street-View.kml">this KML file</a><img border="0" src="http://www.gearthblog.com/images/gelogoicon.gif" title="Google Earth File.  You must have GE installed.">.</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/12/uruguay-gets-street-view.html">Uruguay gets Street View</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:82:"http://www.gearthblog.com/blog/archives/2015/12/uruguay-gets-street-view.html/feed";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:1;a:6:{s:4:"data";s:45:"\n		\n		\n		\n		\n		\n				\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:38:"California City and other ghost cities";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:81:"http://www.gearthblog.com/blog/archives/2015/12/california-city-ghost-cities.html";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:89:"http://www.gearthblog.com/blog/archives/2015/12/california-city-ghost-cities.html#respond";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 03 Dec 2015 09:41:16 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:9:"Site News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:15:"california city";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://www.gearthblog.com/?p=14350";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:577:"<p>We recently watched this YouTube video about California City, which was planned and laid out in the 1960s, but remains largely unoccupied. As you can see in the video, streets were created and named and in some cases even signposted. So we had a look in Google Earth and found that most of the Streets [&#8230;]</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/12/california-city-ghost-cities.html">California City and other ghost cities</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Timothy Whitehead";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:2783:"<p>We recently watched <a href="https://www.youtube.com/watch?v=gO3LUhFwx6k">this YouTube video</a> about California City, which was planned and laid out in the 1960s, but remains largely unoccupied. As you can see in the video, streets were created and named and in some cases even signposted. So we had a look in Google Earth and found that most of the Streets are marked and named in Google Earth.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/California-City.jpg" alt="" /><br />\n<i>Vast areas of desert all marked out with city roads make <a href="https://en.wikipedia.org/wiki/California_City,_California">California City</a> the state&#8217;s third largest city by area.</i></p>\n<p>This reminded us of a location we found in Brazil, which, similarly, has laid out city streets that are visible in imagery dated as far back as 1970. We have been unable to find out anything about it partly because it doesn&#8217;t appear to have a name in Google Earth. The closest marker is &#8220;Sauípe de Dentro&#8221;, which we believe is actually the name of a nearby village. If any of our readers knows anything about it please let us know in the comments.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/Sauípe-de-Dentro.jpg" alt="" /></p>\n<p>This lead us to also investigate other &#8216;ghost cites&#8217; we have heard of in the past, such as <a href="https://en.wikipedia.org/wiki/Kilamba">Kilamba</a> in Angola and the many ghost cities of China. It turns out though that Kilamba has since seen occupation levels rising and most of the ghost cities of China are newly built or still being built and it would be premature to declare that they will not be filled. We did, however, find articles suggesting that China <a href="http://thediplomat.com/2015/10/china-is-still-building-ghost-cities/">is planning to build far more cities</a> than it can ever possibly fill.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/Ordos.jpg" alt="" /><br />\n<i>Ordos, China, is <a href="https://www.chinadialogue.net/article/show/single/en/8239-New-ghost-cities-typify-out-of-control-planning">often cited</a> as an example of a Chinese ghost city.</i></p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/Ordos1.jpg" alt="" /><br />\n<i>The central park or Ordos is quite striking from above.</i></p>\n<p>If any of our readers know of other examples of cites that were partially constructed and then abandoned, please let us know in the comments.</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/12/california-city-ghost-cities.html">California City and other ghost cities</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:86:"http://www.gearthblog.com/blog/archives/2015/12/california-city-ghost-cities.html/feed";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:2;a:6:{s:4:"data";s:57:"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:38:"Where next after the Google Earth API?";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:74:"http://www.gearthblog.com/blog/archives/2015/12/next-google-earth-api.html";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:82:"http://www.gearthblog.com/blog/archives/2015/12/next-google-earth-api.html#respond";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 02 Dec 2015 09:52:08 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:6:{i:0;a:5:{s:4:"data";s:9:"GE Plugin";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:13:"flightradar24";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:8:"GE Teach";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:4:"gefs";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:4;a:5:{s:4:"data";s:19:"Google Earth plugin";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:5;a:5:{s:4:"data";s:15:"rally navigator";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://www.gearthblog.com/?p=14329";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:534:"<p>With the end of the Google Earth API imminent we are having a look at what various sites that depend, or used to depend on the Google Earth API are doing about it. Earlier this year we did a showcase of sites that use the Google Earth API and we will have a look at [&#8230;]</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/12/next-google-earth-api.html">Where next after the Google Earth API?</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Timothy Whitehead";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:4017:"<p>With the <a href="http://www.gearthblog.com/blog/archives/2015/11/google-earth-api-one-month-live.html">end of the Google Earth API imminent</a> we are having a look at what various sites that depend, or used to depend on the Google Earth API are doing about it. Earlier this year <a href="http://www.gearthblog.com/tag/google-earth-plugin-showcase">we did a showcase of sites</a> that use the Google Earth API and we will have a look at a selection of those sites.</p>\n<p>The first is <a href="https://www.rallynavigator.com/how_to_create_cross_country">Rally Navigator</a>. It is a comprehensive online application for creating rally road books, which are a set of instructions used by rally drivers and their co-pilots to map out a course and know what is coming next at each turn of the road. Knowing that the Google Earth API was coming to an end, the developers of Rally Navigator redesigned it to use Google Maps instead. Although it now lacks 3D effects we do not believe it has lost any essential features as a result of the conversion and has gained more features with time. So if you are a rally driver (or co-pilot) then you should definitely check it out. It has both free and paid for versions.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/RallyNavigator.jpg" alt="" /></p>\n<p>Next up is <a href="http://geteach.com/">GETeach</a>. This site was converted to use Google Maps even <a href="http://www.gearthblog.com/blog/archives/2015/01/google-earth-plugin-showcase-ge-teach.html">before we reviewed it</a> earlier this year, but they left the Google Earth version online and it can still be found <a href="http://geteach.com/v5.html">here</a>. GETeach is designed to use as much screen space as possible for the maps, which leaves very little space for menus or other information telling you what it does. So the first thing you should do is view the <a href="http://www.geteach.com/maps/help.html">help page</a> to get an idea of its features and how to access them.</p>\n<p>We do feel that the loss of the Google Earth API in this case does mean the loss of a lot of useful features that simply cannot be replaced by Google Maps, notably the loss of historical imagery and 3D imagery. Although Google Maps does have 3D imagery, it is not accessible via the Google Maps API as is used for this site. Even with the loss of the Google Earth API, however, it is still a great site and would be very useful in the classroom, so be sure to check it out.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/GETeach.jpg" alt="" /></p>\n<p>Next is <a href="http://www.flightradar24.com/">Flightradar24</a>, a flight tracking website that shows the live locations of flights around the globe. It used to have a 3D view, which used the Google Earth plugin to show a model of an aircraft in the correct location on its route. They currently have a message stating that due to the demise of the Google Earth plugin, they are currently working on a replacement 3D experience. We will have to wait till they are done to see how it compares to the old Google Earth API experience.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/flightradar24.jpg" alt="" /></p>\n<p>Last but not least is <a href="http://www.gefs-online.com/">GEFS Online</a>, a popular flight simulator based on the Google Earth API. As far as we can tell, no significant development work has been done on the site for quite some time, with the last blog entry being nearly two years old. We can only guess that there are no current plans to move to an alternative platform and that the site will die along with the Google Earth API.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/GEFSOnline.jpg" alt="" /></p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/12/next-google-earth-api.html">Where next after the Google Earth API?</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:79:"http://www.gearthblog.com/blog/archives/2015/12/next-google-earth-api.html/feed";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:3;a:6:{s:4:"data";s:45:"\n		\n		\n		\n		\n		\n				\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:21:"Air Force One in 3D ?";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:69:"http://www.gearthblog.com/blog/archives/2015/12/air-force-one-3d.html";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:78:"http://www.gearthblog.com/blog/archives/2015/12/air-force-one-3d.html#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 01 Dec 2015 10:31:24 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:11:"Sightseeing";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:8:"airplane";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://www.gearthblog.com/?p=14299";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:535:"<p>Update: Thank you to GEB reader Clare for tracking down this article which lead us to positively identify the aircraft in the 3D imagery as a Boeing E-4B, tail number 31677 as seen here and here. Also there is this YouTube video of it landing in Tallinn, Estonia. It is not one of the two [&#8230;]</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/12/air-force-one-3d.html">Air Force One in 3D ?</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Timothy Whitehead";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:5541:"<p><b>Update: </b> Thank you to GEB reader Clare for tracking down <a href="https://www.quora.com/Air-Force-One/Is-the-Air-Force-One-equipped-to-be-refueled-during-a-flight">this article</a> which lead us to positively identify the aircraft in the 3D imagery as a Boeing E-4B, tail number 31677 as seen <a href="http://planespotterjanb.deviantart.com/art/Boeing-E-4B-747-200B-358904973">here</a> and <a href="http://www.airliners.net/photo/USA---Air/Boeing-E-4B-(747-200B)/0360752/L/">here</a>. Also there is <a href="https://www.youtube.com/watch?v=QPDFBe7MMFg">this YouTube video</a> of it landing in Tallinn, Estonia.  It is not one of the two Boeing VC-25&#8217;s normally used as Air Force One. It is used for transporting senior politicians, amongst other things. For more on the capabilities of E-4B&#8217;s see <a href="https://en.wikipedia.org/wiki/Boeing_E-4">the Wikipedia page</a>.<b>]</b></p>\n<p>Thank you to GEB reader Ryan for letting us know that what appears to be Air Force One has been captured in Google&#8217;s 3D imagery.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/AirForceOne3D.jpg" alt="" /><br />\n<i>Is this one of the aircraft used as Air Force One?</i></p>\n<p>Technically Air Force One is the call sign of any aircraft the President of the United States is on. So, given that the President wasn&#8217;t on this aircraft at the time it can&#8217;t technically be called Air Force One. However, it may be one of two aircraft <a href="https://en.wikipedia.org/wiki/Air_Force_One">currently used as Air Force One</a>. It does appear to be the correct model of aircraft, or at least very similar, but the paint scheme is wrong and although we were unable to read the tail number it does not appear to be either 28000 or 29000 that are the tail numbers of the current aircraft used for Air Force One.</p>\n<p>Based on comparison with historical imagery, the 3D imagery appears to have been captured sometime between February 2013 and March 2014. We can see the same aircraft in the same spot in historical imagery dated February 11th, 2013. In the image dated March 13th, 2014 we can see, in the neighbouring spot, an aircraft that does have the correct paintwork for Air Force One.<br />\n<img src="http://www.gearthblog.com/wp-content/uploads/2015/12/AirForceOne2D.jpg" alt="" /></p>\n<p>The 3D imagery was captured at <a href="https://en.wikipedia.org/wiki/McConnell_Air_Force_Base">McConnell Air Force Base</a>, which is primarily used for air refuelling and airlift and the fleet of Boeing KC-135 Stratotankers can also be seen in the imagery (both 3D and historical).</p>\n<p>According to Wikipedia, Air Force One is usually operated from <a href="https://en.wikipedia.org/wiki/Andrews_Field">Andrews Airfield, Maryland</a>. There, in an October, 2012 image, we can see what appears to be both the Aircraft used as Air Force One:</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/AndrewsAirField2.jpg" alt="" /></p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/AndrewsAirField3.jpg" alt="" /></p>\n<p>And other aircraft with &#8220;United States of America&#8221; painted on the side:</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/AndrewsAirField1.jpg" alt="" /></p>\n<p>And going through historical imagery we can find other examples:</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/AndrewsAirField4.jpg" alt="" /></p>\n<p>Aircraft that previously served as Air Force One are kept in the National Museum of the US Air Force. However, they are kept inside a hanger so they cannot be seen in Google Earth. You can, however, <a href="https://www.google.co.za/maps/@39.7860378,-84.0967748,3a,75y,98.6h,81.16t/data=!3m8!1e1!3m6!1s-K0O9BJuMHlA%2FVhks-Qyzx9I%2FAAAAAAAABG4%2FLJBUOv0sFWc!2e4!3e11!6s%2F%2Flh4.googleusercontent.com%2F-K0O9BJuMHlA%2FVhks-Qyzx9I%2FAAAAAAAABG4%2FLJBUOv0sFWc%2Fw203-h101-n-k-no%2F!7i10240!8i5120">see them in the Google Maps version of Street View</a> or in a Street-View-like <a href="http://www.nmusafvirtualtour.com/full/tour-std.html">virtual tour created by the Museum</a>.</p>\n<p>We can see some other interesting aircraft at the museum that have been captured in 3D:</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/NationalMuseumUSAF1.jpg" alt="" /></p>\n<p>There are also some interesting markings on the runway, whose purpose you can learn more about in <a href="http://www.gearthblog.com/blog/archives/2013/03/barcodes_for_bombers_across_the_uni.html">this post</a></p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/12/NationalMuseumUSAF2.jpg" alt="" /></p>\n<p>The USA is not the only country to designate specific aircraft for the sole use of the head of state. Wikipedia has <a href="https://en.wikipedia.org/wiki/Air_transports_of_heads_of_state_and_government">a list of such Aircraft</a> for a number of other countries. If you are able to find any of them in Google earth imagery please let us know in the comments.</p>\n<p>For the locations featured in this post download <a href="http://www.gearthblog.com/wp-content/uploads/2015/12/Air-Force-One.kml">this KML file</a><img border="0" src="http://www.gearthblog.com/images/gelogoicon.gif" title="Google Earth File.  You must have GE installed.">.</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/12/air-force-one-3d.html">Air Force One in 3D ?</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:74:"http://www.gearthblog.com/blog/archives/2015/12/air-force-one-3d.html/feed";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"2";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:4;a:6:{s:4:"data";s:45:"\n		\n		\n		\n		\n		\n				\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:42:"The best of Google Earth for November 2015";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:84:"http://www.gearthblog.com/blog/archives/2015/11/best-google-earth-november-2015.html";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:93:"http://www.gearthblog.com/blog/archives/2015/11/best-google-earth-november-2015.html#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Mon, 30 Nov 2015 10:16:22 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:9:"Site News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:20:"Best of Google Earth";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://www.gearthblog.com/?p=14277";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:626:"<p>There have been several imagery updates during the month of November, but since Google has not been publishing imagery update maps we cannot determine the full extent of the updates. However, we can, using the Google Earth API, determine the approximate locations of recent imagery, including imagery captured in the month of November. Most of [&#8230;]</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/best-google-earth-november-2015.html">The best of Google Earth for November 2015</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Timothy Whitehead";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:5879:"<p>There have been several imagery updates during the month of November, but since Google has not been publishing imagery update maps we cannot determine the full extent of the updates. However, we can, using the Google Earth API, determine the approximate locations of recent imagery, including imagery captured in the month of November. Most of the November imagery is concentrated in Australia, New Zealand, and southern and eastern Asia. There are a few patches of imagery in West Africa and Brazil. There is no imagery for North America or Europe, most likely because winter does not provide good photographic conditions in those locations. It is fire season in Australia, but we have so far been unable to find any fires or signs of burning in the imagery. There is one image in Ecuador, most likely related to a <a href="http://earthquaketrack.com/quakes/2015-10-15-10-07-28-utc-5-4-104">magnitude 5.4 earthquake</a>, whose epicentre was not far from the location of the image. However, we were unable to see any signs of earthquake damage.</p>\n<p>For a map of the approximate locations of November imagery download <a href="http://www.gearthblog.com/wp-content/uploads/2015/11/November-imagery1.kmz">this KML file</a><img border="0" src="http://www.gearthblog.com/images/gelogoicon.gif" title="Google Earth File.  You must have GE installed.">. If you find anything interesting in the imagery please let us know.</p>\n<p><img width="150" height="150" src="http://www.gearthblog.com/wp-content/uploads/2015/11/ColumbiaFlooding-150x150.jpg" class="alignleft post-image entry-image" alt="Recent imagery updates review" itemprop="image">Earlier in the month we looked at a number of sights that could be found in older imagery, including <a href="http://www.gearthblog.com/blog/archives/2015/11/recent-imagery-updates-review.html">floods in South Carolina</a> and <a href="http://www.gearthblog.com/blog/archives/2015/11/some-sights-we-missed.html">a major flooding event in Japan</a>.<br />\n&nbsp;<br />\n&nbsp;<br />\n&nbsp;</p>\n<p><img width="150" height="150" src="http://www.gearthblog.com/wp-content/uploads/2015/11/levee-150x150.jpg" class="alignleft post-image entry-image" alt="Google floods southern California" itemprop="image">We had a look at <a href="http://www.gearthblog.com/blog/archives/2015/11/google-floods-southern-california.html">a virtual flood in southern California</a> created by a problem with Google Earth&#8217;s water surface incorrectly displaying in an area that is below sea level but is actually dry land.<br />\n&nbsp;<br />\n&nbsp;<br />\n&nbsp;<br />\n&nbsp;</p>\n<p><img width="150" height="150" src="http://www.gearthblog.com/wp-content/uploads/2015/11/NotForSale-150x150.jpg" class="alignleft post-image entry-image" alt="Street View comes to Kampala, Uganda" itemprop="image">We looked at a number of new Street View releases, including <a href="http://www.gearthblog.com/blog/archives/2015/11/street-view-comes-ecuador-bolivia.html">Ecuador and Bolivia</a>, <a href="http://www.gearthblog.com/blog/archives/2015/11/street-view-comes-kampala-uganda.html">Kampala, Uganda</a> and <a href="http://www.gearthblog.com/blog/archives/2015/11/jordan-historical-sites-get-street-view.html">historical sites in Jordan</a>.<br />\n&nbsp;<br />\n&nbsp;<br />\n&nbsp;<br />\n&nbsp;</p>\n<p><img width="150" height="150" src="http://www.gearthblog.com/wp-content/uploads/2015/11/tourmaker-150x150.jpg" class="alignleft post-image entry-image" alt="Tour maker with the Google Earth API" itemprop="image">The Google Earth API <a href="http://www.gearthblog.com/blog/archives/2015/11/google-earth-api-one-month-live.html">is set to stop working</a> on December 12th. Because of this, Paul van Dinther of <a href="http://www.planetinaction.com/">PlanetInAction</a> kindly released two tools that he had that he had created for his own use. The first one is a <a href="http://www.gearthblog.com/blog/archives/2015/11/tour-maker-google-earth-api.html">Tour maker</a> based on the Google Earth API and the second is a program that can <a href="http://www.gearthblog.com/blog/archives/2015/11/controlling-google-earth-via-liquid-galaxy-protocol.html">control Google Earth</a> via the Liquid Galaxy protocol. We also <a href="http://www.gearthblog.com/blog/archives/2015/11/wild-dogs-google-earth-api.html">had a look at a tool called GEGrids</a> that is based on the Google Earth API and has proved useful in mapping land use in East Africa, which is aiding in the study of wildlife in the region.</p>\n<p><img width="150" height="150" src="http://www.gearthblog.com/wp-content/uploads/2015/11/Thanksgiving2-150x150.jpg" class="alignleft post-image entry-image" alt="Creating Thanksgiving cards with Google Earth" itemprop="image">As <a href="http://www.gearthblog.com/tag/thanksgiving">we traditionally do</a>, we celebrated Thanksgiving with a Google Earth Thanksgiving card and <a href="http://www.gearthblog.com/blog/archives/2015/11/creating-thanksgiving-cards-google-earth.html">showed you how to create your own</a>.<br />\n&nbsp;<br />\n&nbsp;<br />\n&nbsp;<br />\n&nbsp;</p>\n<p><img width="150" height="150" src="http://www.gearthblog.com/wp-content/uploads/2015/11/Planet-Labs-150x150.jpg" class="alignleft post-image entry-image" alt="Planet Labs progress" itemprop="image">We had a look at <a href="http://www.gearthblog.com/blog/archives/2015/11/planet-labs-progress.html">Planet Labs&#8217; progress</a> and had a look at their imagery of Southern California, which they have made available under the <a href="https://creativecommons.org/licenses/by-sa/4.0/">creative commons licence</a>.</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/best-google-earth-november-2015.html">The best of Google Earth for November 2015</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:89:"http://www.gearthblog.com/blog/archives/2015/11/best-google-earth-november-2015.html/feed";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"6";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:5;a:6:{s:4:"data";s:51:"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:55:"Controlling Google Earth via the Liquid Galaxy protocol";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:104:"http://www.gearthblog.com/blog/archives/2015/11/controlling-google-earth-via-liquid-galaxy-protocol.html";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:113:"http://www.gearthblog.com/blog/archives/2015/11/controlling-google-earth-via-liquid-galaxy-protocol.html#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 27 Nov 2015 09:46:42 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:4:{i:0;a:5:{s:4:"data";s:9:"Site News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:13:"liquid galaxy";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:16:"paul van dinther";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:18:"planetinaction.com";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://www.gearthblog.com/?p=14261";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:632:"<p>Given that the Google Earth API is coming to an end we are looking at alternative solutions. Yesterday we had a look at Google Earth&#8217;s internal browser and what its capabilities are. Today we are looking at a way of controlling Google Earth from another application. Liquid Galaxy is the name given to a Google [&#8230;]</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/controlling-google-earth-via-liquid-galaxy-protocol.html">Controlling Google Earth via the Liquid Galaxy protocol</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Timothy Whitehead";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:5728:"<p>Given that the Google Earth API <a href="http://www.gearthblog.com/blog/archives/2015/11/google-earth-api-one-month-live.html">is coming to an end</a> we are looking at alternative solutions. Yesterday <a href="http://www.gearthblog.com/blog/archives/2015/11/google-earths-internal-browser.html">we had a look at Google Earth&#8217;s internal browser</a> and what its capabilities are. Today we are looking at a way of controlling Google Earth from another application.</p>\n<p><a href="http://www.google.co.za/earth/explore/showcase/liquidgalaxy.html">Liquid Galaxy</a> is the name given to a Google Earth demonstration using multiple displays to give an immersive experience. To see it in action, see the photos and videos on of various installations <a href="https://code.google.com/p/liquid-galaxy/wiki/LiquidGalaxyGallery">here</a>. Liquid Galaxy works by allowing Google Earth on one computer to communicate with instances of Google Earth on other computers and synchronize their views so as to give the impression that there is a single instance of Google Earth running across multiple screens. To achieve this, Google Earth has a special Liquid Galaxy protocol that can be used to instruct Google Earth to show a particular location from a particular angle. It is possible to use this protocol for uses other than Liquid Galaxy installations.</p>\n<p>Paul van Dinther of <a href="http://www.planetinaction.com/">PlanetInAction</a> has kindly released a small program that demonstrates this functionality. It is a Windows only program called GExplorer and it replicates the functionality of <a href="http://www.planetinaction.com/gexplorer/">a JavaScript by the same name</a> he created that works with the Google Earth plugin.</p>\n<p>See this YouTube video to get an idea of what it does:<br />\n<span class=''embed-youtube'' style=''text-align:center; display: block;''><iframe class=''youtube-player'' type=''text/html'' width=''640'' height=''390'' src=''http://www.youtube.com/embed/haf51tuUZzk?version=3&#038;rel=1&#038;fs=1&#038;autohide=2&#038;showsearch=0&#038;showinfo=1&#038;iv_load_policy=1&#038;wmode=transparent'' frameborder=''0'' allowfullscreen=''true''></iframe></span></p>\n<p>To try it for yourself first download the program <a href="http://www.gearthblog.com/js/geexplorer.exe">here</a> (Windows only).</p>\n<p>Next, you need to configure Google Earth to accept Liquid Galaxy protocol instructions. To do this you need to edit the drivers.ini file found in the Google Earth program folder. The file is typically found in the folder<br />\n<code>C:\\Program Files (x86)\\Google\\Google Earth\\client</code> (for Google Earth)<br />\nor<br />\n<code>C:\\Program Files (x86)\\Google\\Google Earth Pro\\client</code> (for Google Earth Pro)</p>\n<p>We found Windows Notepad does not display the file properly so you will need a more advanced editor. We used SciTE which can be obtained from <a href="http://www.scintilla.org/SciTE.html">here</a>. Edit the drivers.ini file and insert the following lines at the start of the SETTINGS section.</p>\n<p><code>    ViewSync/send = false<br />\n    ViewSync/receive = true<br />\n    ViewSync/port = 21567<br />\n    ViewSync/yawOffset = 0<br />\n    ViewSync/pitchOffset = 0.0<br />\n    ViewSync/rollOffset = 0.0<br />\n    ViewSync/horizFov = 60<br />\n</code></p>\n<p>It should look like this:<br />\n<img src="http://www.gearthblog.com/wp-content/uploads/2015/11/driversini.jpg" alt="" /></p>\n<p>Windows will not let you save it directly to the Google Earth program folder so save it on your desktop and then copy it to the Google Earth program folder.</p>\n<p>Start Google Earth and GExplorer and make sure the GExplorer window is on the same monitor as Google Earth.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/11/GEXplorer.jpg" alt="" /></p>\n<p>Initially nothing will happen because GExplorer is broadcasting to the wrong IP address. Click on the &#8220;Get my broadcast IP&#8221; button to adjust the IP address or type it in if you have different network requirements (Such as an actual Liquid Galaxy setup with multiple computers). If all is well it should say &#8220;UDP Connected&#8221; in red.</p>\n<p>Next click &#8220;Start&#8221;.</p>\n<p>The Google Earth view should now leap to the start latitude and longitude location. You will also notice that the view moves around as you move your mouse. You can now control Google Earth with a combination of the WASD keys and the mouse. SHIFT and CTRL can also be used to control height. Amplify any control input by holding down the spacebar with any combination of the above keys.</p>\n<p>The view animates smoothly based on control inputs. The responsiveness can be modified with the &#8220;Inertia&#8221; dropdown. Low numbers means more responsive.</p>\n<p>&#8220;Tilt limit&#8221; specifies the maximum angle you can look up or down. Try changing it to 90.</p>\n<p>As you turn left or right the camera automatically rolls like an aircraft. If you don&#8217;t want this behaviour you can turn it off by ticking the &#8220;No roll&#8221; checkbox.</p>\n<p>You can also switch between &#8220;Earth&#8221;, &#8220;Moon&#8221; or &#8220;Mars&#8221; by changing a dropdown.</p>\n<p>Paul also suggests the great idea of creating a mobile app with the same functionality which would allow you to control Google Earth from your mobile phone. If any of our readers creates such an app please let us know about it in the comments.</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/controlling-google-earth-via-liquid-galaxy-protocol.html">Controlling Google Earth via the Liquid Galaxy protocol</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:109:"http://www.gearthblog.com/blog/archives/2015/11/controlling-google-earth-via-liquid-galaxy-protocol.html/feed";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"2";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:6;a:6:{s:4:"data";s:45:"\n		\n		\n		\n		\n		\n				\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:33:"Google Earth’s internal browser";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:83:"http://www.gearthblog.com/blog/archives/2015/11/google-earths-internal-browser.html";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:92:"http://www.gearthblog.com/blog/archives/2015/11/google-earths-internal-browser.html#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 26 Nov 2015 10:53:40 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:9:"Site News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:16:"internal browser";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://www.gearthblog.com/?p=14236";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:575:"<p>With the demise of the Google Earth API fast approaching we are looking at what alternatives are available. For some use cases, one possibility is to try and implement what you want to do within Google Earth rather than with the Google Earth plugin. In order to see what is possible we need to know [&#8230;]</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/google-earths-internal-browser.html">Google Earth&#8217;s internal browser</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Timothy Whitehead";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:3390:"<p>With the <a href="http://www.gearthblog.com/blog/archives/2015/11/google-earth-api-one-month-live.html">demise of the Google Earth API fast approaching</a> we are looking at what alternatives are available. For some use cases, one possibility is to try and implement what you want to do within Google Earth rather than with the Google Earth plugin. In order to see what is possible we need to know more about the capabilities of Google Earth&#8217;s internal web browser.</p>\n<p>Google Earth&#8217;s internal browser is most obvious when you open a link in a Placemark or if you click the &#8220;Earth Gallery&#8221; button found at the top of the Layers section. Depending on your settings this will open a web page in Google Earth. However, we prefer to have Google Earth open links in our normal browser. You can achieve this by turning on the setting <b>&#8220;Tools->Options->General->Display->Show web results in external browser&#8221;</b>.</p>\n<p>What we are most interested in is the fact that Google Earth&#8217;s internal browser is used in Placemark popups. Because Placemarks are rendered using a browser you can do many things in them that you can do in a web page. We <a href="http://www.gearthblog.com/blog/archives/2015/06/adobe-flash-google-earth.html">have seen in the past</a> that it is possible to use Adobe Flash in Placemarks and one of the built-in layers uses this functionality. However, most people will not have it installed and may have difficulty installing it. JavaScript, on the other hand, works well and should work for everyone.</p>\n<p>So what browser does Google Earth use? We can put a little JavaScript in a popup and it will tell us:<br />\n<code>Mozilla/5.0 (Windows; N; ; en-US) AppleWebKit/532.4 (KHTML, like Gecko) Google Earth Pro/7.1.5.1557 Safari/532.4</code></p>\n<p>For comparison the current version of Chrome says:<br />\n<code>Mozilla/5.0 (Windows NT 10.0; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/46.0.2490.86 Safari/537.36</code></p>\n<p>The important part is &#8220;AppleWebKit/532.4&#8221;, which tells us that it is based on the same layout engine as Google Chrome, Safari and Opera. However, it is an older version than the one currently found in those browsers.</p>\n<p>Popups also support the IFrame tag, which allows you to insert any external website into a popup. This means that we can easily do a feature test with the website <a href="https://html5test.com/">html5test.com</a>.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/11/HTML5Test.jpg" alt="" /></p>\n<p>As you can see above, it is not very HTML 5 compatible, attaining a score of only 119 out of 555, whereas the current version of Google Chrome scores 521 out of 555.</p>\n<p>To try the above tests for yourself in Google Earth download <a href="http://www.gearthblog.com/wp-content/uploads/2015/11/Internal-browser-tests.kml">this KML file</a><img border="0" src="http://www.gearthblog.com/images/gelogoicon.gif" title="Google Earth File.  You must have GE installed.">.</p>\n<p>In future posts we will explore what functionality can be achieved with popups and JavaScript.</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/google-earths-internal-browser.html">Google Earth&#8217;s internal browser</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:88:"http://www.gearthblog.com/blog/archives/2015/11/google-earths-internal-browser.html/feed";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:7;a:6:{s:4:"data";s:45:"\n		\n		\n		\n		\n		\n				\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:45:"Creating Thanksgiving cards with Google Earth";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:93:"http://www.gearthblog.com/blog/archives/2015/11/creating-thanksgiving-cards-google-earth.html";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:101:"http://www.gearthblog.com/blog/archives/2015/11/creating-thanksgiving-cards-google-earth.html#respond";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 25 Nov 2015 11:06:03 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:9:"Site News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:12:"thanksgiving";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://www.gearthblog.com/?p=14218";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:635:"<p>Back in 2006 Frank created a Thanksgiving card for Google Earth. Today we are looking at how to go about creating your own Thanksgiving greetings cards with Google Earth. Firstly, you need an image with your Thanksgiving greeting. The easiest is to simply find a suitable image by doing a Google image search for &#8216;Thanksgiving&#8217;. [&#8230;]</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/creating-thanksgiving-cards-google-earth.html">Creating Thanksgiving cards with Google Earth</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Timothy Whitehead";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:3227:"<p>Back in 2006 Frank created <a href="http://www.gearthblog.com/blog/archives/2006/11/happy_thanksgiving_i.html">a Thanksgiving card for Google Earth</a>. Today we are looking at how to go about creating  your own Thanksgiving greetings cards with Google Earth.</p>\n<p>Firstly, you need an image with your Thanksgiving greeting. The easiest is to simply find a suitable image by doing a Google image search for &#8216;Thanksgiving&#8217;. Keep in mind that there may be copyright restrictions on such images, so they should really only be for personal use. Alternatively, you can get a bit more personal by creating your own image. That way you can write your own unique message. Ideally, you want to end up with an image that is roughly the correct proportions for the location you want to display it and also has a transparent background. Keep in mind that to use transparent backgrounds you need to save the image as a .png or .gif. We used Microsoft Word to create the image then took a screen shot and used <a href="https://www.gimp.org/">Gimp</a> to give it a transparent background. We used some clipart from <a href="https://openclipart.org/">OpenClipart.org</a>.</p>\n<p>Next, you simply create an image overlay in Google Earth (click this icon <img src="http://www.gearthblog.com/wp-content/uploads/2015/11/ImageOverlayIcon.png" alt="" /> on the toolbar). Adjust its size and location to suit and select your image to be shown.</p>\n<p>If you wish to send your greeting to someone else be sure to save the image overlay as a KMZ file so that it includes the image in the file. KML files do not include images.</p>\n<p>If you want to go all out, you could also decorate the sky, <a href="http://www.gearthblog.com/blog/archives/2014/10/halloween.html">as we did for Halloween</a>. Just download <a href="http://www.gearthblog.com/wp-content/uploads/2015/11/StarsOverlay.kmz">this file</a><img border="0" src="http://www.gearthblog.com/images/gelogoicon.gif" title="Google Earth File.  You must have GE installed."> and replace the image in the overlay with your own Thanksgiving themed image.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/11/Thanksgiving1.jpg" alt="" /><br />\n<i>Frank&#8217;s original Thanksgiving message. See it for yourself in Google Earth with <a href="http://www.gearthblog.com/kmfiles/turkeyday.kmz">this KMZ file</a><img border="0" src="http://www.gearthblog.com/images/gelogoicon.gif" title="Google Earth File.  You must have GE installed."><br />\n</i></p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/11/Thanksgiving2.jpg" alt="" /><br />\n<i>An updated version showing how easy it is to do. See it for yourself in Google Earth with <a href="http://www.gearthblog.com/wp-content/uploads/2015/11/Happy-Thanksgiving.kmz">this KMZ file</a><img border="0" src="http://www.gearthblog.com/images/gelogoicon.gif" title="Google Earth File.  You must have GE installed."><br />\n</i></p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/creating-thanksgiving-cards-google-earth.html">Creating Thanksgiving cards with Google Earth</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:98:"http://www.gearthblog.com/blog/archives/2015/11/creating-thanksgiving-cards-google-earth.html/feed";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:8;a:6:{s:4:"data";s:48:"\n		\n		\n		\n		\n		\n				\n		\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:39:"Jordan historical sites get Street View";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:92:"http://www.gearthblog.com/blog/archives/2015/11/jordan-historical-sites-get-street-view.html";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:100:"http://www.gearthblog.com/blog/archives/2015/11/jordan-historical-sites-get-street-view.html#respond";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 24 Nov 2015 10:35:49 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:3:{i:0;a:5:{s:4:"data";s:11:"Street View";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:6:"Jordan";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:11:"street view";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://www.gearthblog.com/?p=14210";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:601:"<p>Google has just released Street View for more than 30 historical sites in Jordan. Read more about it on the LatLong Blog. It can be a little difficult to explore the locations in Google Earth. As we have mentioned before, Google Earth has some problems with the blue Street View layer not showing correct coverage [&#8230;]</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/jordan-historical-sites-get-street-view.html">Jordan historical sites get Street View</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Timothy Whitehead";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:2675:"<p>Google has just released Street View for more than 30 historical sites in Jordan. Read more about it on the <a href="http://google-latlong.blogspot.co.za/">LatLong Blog</a>.</p>\n<p><span class=''embed-youtube'' style=''text-align:center; display: block;''><iframe class=''youtube-player'' type=''text/html'' width=''640'' height=''390'' src=''http://www.youtube.com/embed/yXdYec1Z8PQ?version=3&#038;rel=1&#038;fs=1&#038;autohide=2&#038;showsearch=0&#038;showinfo=1&#038;iv_load_policy=1&#038;wmode=transparent'' frameborder=''0'' allowfullscreen=''true''></iframe></span></p>\n<p>It can be a little difficult to explore the locations in Google Earth. As <a href="http://www.gearthblog.com/blog/archives/2015/08/blue-street-view-layer.html">we have mentioned before</a>, Google Earth has some problems with the blue Street View layer not showing correct coverage at some zoom levels. When you hold the &#8216;yellow man&#8217; above the map, Google Earth shows blue outlines marking areas with Street View. This is achieved by having a set of blue outline layers with different amounts of detail to be shown depending on what distance you are viewing the earth from. However, it appears that Google either fails to update some of the layers or has a problem with their algorithm for creating it. Although the new Street View in Jordan can be identified from a high altitude the moment you start to zoom in the blue disappears and it can be quite difficult to actually enter the Street View for many of the locations.</p>\n<p>An additional problem is that once you are in Street View there are no indications as to the extent of the coverage or where you can navigate, so you just have to click in a direction and see if you can go there.</p>\n<p>We therefore recommend exploring the new Street View in Google Maps. If you do prefer Google Earth then we have created <a href="http://www.gearthblog.com/wp-content/uploads/2015/11/Jordan-Street-View.kmz">some placemarks</a><img border="0" src="http://www.gearthblog.com/images/gelogoicon.gif" title="Google Earth File.  You must have GE installed."> for a number of the sites to get you started.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/11/LittlePetra.jpg" alt="" /><br />\n<i>Little Petra</i></p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/11/UmmQaisArchaeologicalSite.jpg" alt="" /><br />\n<i>Umm Qais Archaeological Site</i></p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/jordan-historical-sites-get-street-view.html">Jordan historical sites get Street View</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:97:"http://www.gearthblog.com/blog/archives/2015/11/jordan-historical-sites-get-street-view.html/feed";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:9;a:6:{s:4:"data";s:48:"\n		\n		\n		\n		\n		\n				\n		\n		\n\n		\n		\n				\n			\n		\n		";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:34:"Wild Dogs and the Google Earth API";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:79:"http://www.gearthblog.com/blog/archives/2015/11/wild-dogs-google-earth-api.html";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:88:"http://www.gearthblog.com/blog/archives/2015/11/wild-dogs-google-earth-api.html#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Mon, 23 Nov 2015 10:36:59 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:3:{i:0;a:5:{s:4:"data";s:9:"GE Plugin";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:9:"Site News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:19:"Google Earth plugin";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://www.gearthblog.com/?p=14189";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:594:"<p>We recently came across this interesting article about how researchers have created a land cover map of East Africa, differentiating the areas with human land cover vs areas that remain natural. The map will be useful for studying many different species of wildlife in the area, but it has already proved useful in the study [&#8230;]</p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/wild-dogs-google-earth-api.html">Wild Dogs and the Google Earth API</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Timothy Whitehead";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:1761:"<p>We recently came across <a href="http://voices.nationalgeographic.com/2015/11/19/ge-grids/">this interesting article</a> about how researchers have created a land cover map of East Africa, differentiating the areas with human land cover vs areas that remain natural. The map will be useful for studying many different species of wildlife in the area, but it has already proved useful in the study of African Wild Dogs.</p>\n<p>The map was created using a tool called GE Grids created by Andy Stanish and available <a href="http://andrewstanish.com/files/GERasterCreator.zip">here</a>. It creates a grid over a specified area and you can select or de-select squares in the grid. So, for the East Africa map in the article above researchers looked at the satellite imagery and selected areas that showed signs of human habitation or land use. Very simple but remarkably useful. This highlights the usefulness of the Google Earth API, which <a href="http://www.gearthblog.com/blog/archives/2015/11/google-earth-api-one-month-live.html">Google plans to shut down next month</a>. In this particular case the tool does not use historical imagery or other features unique to Google Earth, so it would be relatively easy to convert the tool to use Google Maps instead. However, there are plenty of other applications and  tools out there which will have no real alternative after the Google Earth API comes to an end.</p>\n<p><img src="http://www.gearthblog.com/wp-content/uploads/2015/11/GEGrids.jpg" alt="" /></p>\n<p>The post <a rel="nofollow" href="http://www.gearthblog.com/blog/archives/2015/11/wild-dogs-google-earth-api.html">Wild Dogs and the Google Earth API</a> appeared first on <a rel="nofollow" href="http://www.gearthblog.com">Google Earth Blog</a>.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:84:"http://www.gearthblog.com/blog/archives/2015/11/wild-dogs-google-earth-api.html/feed";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}s:27:"http://www.w3.org/2005/Atom";a:1:{s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:3:{s:4:"href";s:30:"http://www.gearthblog.com/feed";s:3:"rel";s:4:"self";s:4:"type";s:19:"application/rss+xml";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:44:"http://purl.org/rss/1.0/modules/syndication/";a:2:{s:12:"updatePeriod";a:1:{i:0;a:5:{s:4:"data";s:6:"hourly";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:15:"updateFrequency";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}}}}}}s:4:"type";i:128;s:7:"headers";a:12:{s:4:"date";s:29:"Sat, 05 Dec 2015 16:48:48 GMT";s:6:"server";s:103:"Apache/2.2.25 (Unix) mod_ssl/2.2.25 OpenSSL/1.0.0-fips DAV/2 mod_auth_passthrough/2.1 mod_bwlimited/1.4";s:12:"x-powered-by";s:10:"PHP/5.2.17";s:10:"x-pingback";s:36:"http://www.gearthblog.com/xmlrpc.php";s:4:"etag";s:34:""c0b07d76732d77134e0670cf59c95a3b"";s:12:"x-robots-tag";s:15:"noindex, follow";s:13:"last-modified";s:29:"Fri, 04 Dec 2015 10:02:13 GMT";s:4:"vary";s:26:"Accept-Encoding,User-Agent";s:16:"content-encoding";s:4:"gzip";s:14:"content-length";s:5:"12520";s:10:"connection";s:5:"close";s:12:"content-type";s:34:"application/rss+xml; charset=UTF-8";}s:5:"build";s:14:"20130911110210";}', 'no');
