6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:62:"https://en-blog.files.wordpress.com/2015/12/img_4997.jpg?w=300";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:29:"http://search.yahoo.com/mrss/";a:1:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:8:"img_4997";s:7:"attribs";a:1:{s:0:"";a:1:{s:4:"type";s:4:"html";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:7;a:6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:102:"https://en-blog.files.wordpress.com/2015/12/10385463_10154373034305215_8972420320531447358_n.jpg?w=225";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:29:"http://search.yahoo.com/mrss/";a:1:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:48:"10385463_10154373034305215_8972420320531447358_n";s:7:"attribs";a:1:{s:0:"";a:1:{s:4:"type";s:4:"html";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}}}i:4;a:6:{s:4:"data";s:68:"\n		\n		\n		\n		\n		\n				\n		\n\n		\n		\n				\n			\n		\n	\n		\n		\n\n		\n\n		\n\n		\n\n		\n	";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:6:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:16:"New Theme: Pique";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:47:"https://en.blog.wordpress.com/2015/12/17/pique/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:56:"https://en.blog.wordpress.com/2015/12/17/pique/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 17 Dec 2015 16:00:10 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:6:"Themes";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:11:"free themes";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:37:"http://en.blog.wordpress.com/?p=33767";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:244:"Say hello to Pique, a brand new free theme for small businesses!<img alt="" border="0" src="https://pixel.wp.com/b.gif?host=en.blog.wordpress.com&#038;blog=3584907&#038;post=33767&#038;subd=en.blog&#038;ref=&#038;feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"sarah semark";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:2592:"<p>Today, we&#8217;re welcoming a new free theme to our collection: <em>Pique</em>.</p>\n<h3><a href="http://wordpress.com/themes/pique">Pique</a></h3>\n<p><a href="http://piquedemo.wordpress.com"><img src="https://theme.files.wordpress.com/2015/12/front-page.png?w=1200&#038;h=2379" alt="Pique''s front page design" width="1200" height="2379" /></a></p>\n<p><em>Pique</em> is a stylish and modern theme designed as a one-page scrolling site, giving your customers easy access to all your information in one place. Personalize your site by adding featured images to your pages and tweaking the colors to create a site that&#8217;s perfectly suited to your brand. Upload your logo and custom header image to make your site distinctive.</p>\n<p>Designed specifically with coffee shops in mind, <em>Pique</em> is particularly well-suited for small, independent businesses. Use it to build an online home for your bakery, hair salon, shop, or cafe.</p>\n<p>And of course, it&#8217;s completely responsive, so it will look good on any device your customers are using, whether they&#8217;re on the road or at home.</p>\n<p><a href="http://piquedemo.wordpress.com"><img src="https://theme.files.wordpress.com/2015/12/pique-responsive.png?w=720" alt="Pique on various devices" /></a></p>\n<p><em>Pique</em> has lots of fantastic features that will allow you to create a beautiful and dynamic showcase for your business, including:</p>\n<ul>\n<li>Upload your own Site Logo</li>\n<li>Customer testimonials</li>\n<li>Optional sidebar on post pages</li>\n<li>Custom menus in both the header and footer</li>\n<li>Custom headers and backgrounds</li>\n<li>Featured images on posts and pages</li>\n<li>A large footer with three widget areas</li>\n<li>Social menu support</li>\n</ul>\n<p>Single posts in <em>Pique</em> are clean and elegant and feature an optional sidebar for additional content.</p>\n<p><a href="http://piquedemo.wordpress.com"><img src="https://theme.files.wordpress.com/2015/12/single-post.png?w=720" alt="Single post page" /></a></p>\n<p>Give <em>Pique</em> a spin on the <a href="https://wordpress.com/themes/pique">Theme Showcase</a>!</p><br />Filed under: <a href=''https://en.blog.wordpress.com/category/themes/''>Themes</a>  <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gocomments/en.blog.wordpress.com/33767/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/comments/en.blog.wordpress.com/33767/" /></a> <img alt="" border="0" src="https://pixel.wp.com/b.gif?host=en.blog.wordpress.com&#038;blog=3584907&#038;post=33767&#038;subd=en.blog&#038;ref=&#038;feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:52:"https://en.blog.wordpress.com/2015/12/17/pique/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:2:"21";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:2:{s:9:"thumbnail";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:1:{s:3:"url";s:69:"http://en-blog.files.wordpress.com/2015/12/pique-responsive.png?w=150";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"content";a:5:{i:0;a:6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:69:"http://en-blog.files.wordpress.com/2015/12/pique-responsive.png?w=150";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:29:"http://search.yahoo.com/mrss/";a:1:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:16:"Pique-Responsive";s:7:"attribs";a:1:{s:0:"";a:1:{s:4:"type";s:4:"html";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:1;a:6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:75:"https://0.gravatar.com/avatar/fa11bb9a85270cb1be8d15ede976e4bc?s=96&d=retro";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:29:"http://search.yahoo.com/mrss/";a:1:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:11:"sarahsemark";s:7:"attribs";a:1:{s:0:"";a:1:{s:4:"type";s:4:"html";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:2;a:6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:56:"https://theme.files.wordpress.com/2015/12/front-page.png";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:29:"http://search.yahoo.com/mrss/";a:1:{s:5:"title"